INSERT INTO `mod_da00411bc15ca9ae98ca2db1f3df7a7a_master_repo` VALUES (226,4,'Tomb Raider Movie Location Confirmed: Angkor Wat, Cambodia','1',979768801,'Tombraiders.com',1,1,1,'1,2'),(223,4,'Angelina on Filming in Cambodia','2',980978401,'Tombraiders.com',1,1,0,''),(219,4,'LA Times Interview with Angelina Jolie and Simon West','3',983451600,'Los Angeles Times',1,1,0,''),(220,4,'Tomb Raider Movie and Game to Be Linked Together','4',983570401,'Tombraiders.com',1,1,0,''),(221,4,'Dad, Jon Voight, Wishes Angelina Would Take It Easy','5',984002401,'Tombraiders.com',1,1,0,''),(228,4,'Jon Voight, Angelina&#039;s Father, to Play Lara Croft&#039;s Father','6',985266000,'Tombraiders.com',1,1,1,'3'),(222,4,'Jeremy Heath-Smith Keeps an Eye On the Movie.','7',986072401,'Tombraiders.com',1,1,0,''),(224,4,'Entertainment Online Interview with Angelina Jolie','8',989139600,'Tombraiders.com',1,1,0,''),(225,4,'Angelina Is Amazed by Cambodia','9',989960401,'Tombraiders.com',1,1,1,'4'),(227,4,'Tomb Raider Movie Films Stolen','10',990565201,'IGN',1,1,0,''),(218,4,'Angelina in Action','11',991472400,'Hollywood.com',1,1,0,''),(299,4,'Check Out The UK Tomb Raider Premiere&#033;','12',993416401,'United International Pictures',1,1,1,'5,6,7,8'),(301,4,'Tomb Raider Protest in India','13',996656400,'REUTERS',1,1,1,''),(300,17,'Tomb Raider: The Ride - Online Auction','14',1014987600,'TombRaider.Ws',1,1,1,''),(352,11,'Interview with Adrian Smith, Operations Director At Core Design','15',1023872400,'TombRaider.Com [Official Site]',1,1,0,''),(353,11,'Interview with Mike Schmidt, Producer At Eidos Interactive','16',1023872400,'IGN',1,1,0,''),(287,5,'Let s Raid Again&#033;','17',1025514000,'RedDwarf.com',1,1,1,''),(298,15,'Angelina Jolie Becomes UN Ambassador For Refugees','18',1028192400,'TombRaider.Ws',1,1,1,''),(308,1,'Meet &#039;Chyna&#039; - Beijing&#039;s answer to Lara Croft','19',1028278800,'China Daily',1,1,1,''),(282,5,'Tomb Raider Sequel Filmed in Wales, UK','20',1030525200,'BBC',1,1,1,''),(