INSERT INTO `skin_sets` VALUES ('1','Tomb Raider',0,1,'shehi@imanov.name','Shahriyar Imanov','http://www.audith.com','a:6:{i:0;a:4:{s:4:\"file\";s:14:\"/jquery-ui.css\";s:6:\"params\";s:0:\"\";s:4:\"type\";s:3:\"css\";s:5:\"scope\";s:6:\"global\";}i:1;a:4:{s:4:\"file\";s:10:\"/style.css\";s:6:\"params\";s:0:\"\";s:4:\"type\";s:3:\"css\";s:5:\"scope\";s:5:\"local\";}i:2;a:4:{s:4:\"file\";s:10:\"/jquery.js\";s:6:\"params\";s:0:\"\";s:4:\"type\";s:2:\"js\";s:5:\"scope\";s:6:\"global\";}i:3;a:4:{s:4:\"file\";s:13:\"/jquery-ui.js\";s:6:\"params\";s:0:\"\";s:4:\"type\";s:2:\"js\";s:5:\"scope\";s:6:\"global\";}i:4;a:4:{s:4:\"file\";s:10:\"/global.js\";s:6:\"params\";s:0:\"\";s:4:\"type\";s:2:\"js\";s:5:\"scope\";s:6:\"global\";}i:5;a:4:{s:4:\"file\";s:10:\"/global.js\";s:6:\"params\";s:0:\"\";s:4:\"type\";s:2:\"js\";s:5:\"scope\";s:5:\"local\";}}',NULL,0,1);
