  `t_group_name` varchar(50) COLLATE utf8_unicode_ci NOT NULL,
  `t_bit_name` varchar(50) COLLATE utf8_unicode_ci NOT NULL,
  `t_timestamp` int(10) unsigned DEFAULT NULL,
  `t_source` mediumtext COLLATE utf8_unicode_ci,
  `t_type` varchar(8) COLLATE utf8_unicode_ci NOT NULL DEFAULT 'tpl',
  `t_can_remove` tinyint(1) NOT NULL DEFAULT '0',
  PRIMARY KEY (`t_id`),
  KEY `s_set_id` (`s_set_id`),
  CONSTRAINT `skin_templates_ibfk_1` FOREIGN KEY (`s_set_id`) REFERENCES `skin_sets` (`set_id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=58 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci ROW_FORMAT=DYNAMIC;
