INSERT INTO `skin_templates` VALUES (1,'1',NULL,'global','copyright',1329812161,'<p id=\"disclaimer\">Copyright &copy; 2001 - 2012 Shahriyar Imanov. All Rights Reserved.</p>','tpl',0),(2,'1',NULL,'global','css_inline',1272547216,'{{foreach from=$CSS_CONTENT key=K item=V}}\r\n{{$V.content}}\r\n{{foreachelse}}\r\n{{/foreach}}','tpl',0),(3,'1',NULL,'global','favicon',1329812193,'{{if $FAVICON neq \'\'}}\r\n<link rel=\"shortcut icon\" href=\"{{$FAVICON}}\" />\r\n{{/if}}','tpl',0),(4,'1',NULL,'global','footer',1329812217,'<p id=\"footer\"><a href=\"#\">forums</a> | <a href=\"#\">disclaimer</a> | <a href=\"#\">terms &amp; guidelines</a></p>','tpl',0),(5,'1',NULL,'global','header',1329812245,'<div id=\"header\" style=\"background-image: url(\'{{$STYLE_IMAGES_URL}}/header_image_{{math equation=\"rand(1,14)\"}}.jpg\');\"></div>','tpl',0),(6,'1',NULL,'global','javascript_inline',1272547216,'{{foreach from=$JS_CONTENT key=K item=V}}\r\n{{$V.content}}\r\n{{foreachelse}}\r\n{{/foreach}}','tpl',0),(7,'1',NULL,'global','logo',1329811691,'<h1 id=\"logo\"><a href=\"{{$SITE_URL}}\">{{$SITE_NAME}}{{if $SITE_MOTTO neq \'\'}} - {{$SITE_MOTTO}}{{/if}}</a></h1>','tpl',0),(8,'1',NULL,'global','memberbar',1329811650,'<ul id=\"user_navigation\">\r\n	{{if $MEMBER.id}}\r\n	<li>Logged-in As: <a href=\"/users/profile-{{$MEMBER.id}}-{{$MEMBER.seo_name}}\" title=\"View profile for {{$MEMBER.display_name}}\" id=\"links__memberbar__profile\">{{$MEMBER.display_name}}</a></li>\r\n	<li><a href=\"/users/logout?referer={{$PAGE.request.request_uri|urlencode}}\" title=\"Logout\" rel=\"nofollow\">Logout</a></li>\r\n	{{else}}\r\n	<li><a href=\"/users/login\" title=\"Login\" rel=\"nofollow\">Login</a></li>\r\n	<li><a href=\"/users/lostpass\" title=\"Lost Password?\" rel=\"nofollow\">Lost Password?</a></li>\r\n	<li><a href=\"/users/register\" title=\"Register\" rel=\"nofollow\">Register</a></li>\r\n	{{/if}}\r\n</ul>','tpl',0),(10,'1',NULL,'global','meta_generator',1329811621,'<meta name=\"keywords\"                 content=\"{{$GENERATOR.keywords}}\" />\r\n<meta name=\"description\"              content=\"{{$GENERATOR.description}}\" />\r\n<meta http-equiv=\"content-type\"       content=\"text/html; charset=utf-8\" />\r\n<meta name=\"robots\"                   content=\"{{$GENERATOR.robots}}\" />\r\n<meta name=\"verify-v1\"                content=\"{{$GENERATOR.google_verify_v1}}\" />','tpl',0),(11,'1',NULL,'global','navigation',1329811588,'<ul id=\"nav\">\r\n	<li style=\"width:113px; height:86px; float:left; overflow:hidden; background-image:url(\'{{$STYLE_IMAGES_URL}}/top_menu_tombraider.jpg\'); background-repeat:no-repeat;\"><a href=\"{{$SITE_URL}}/tombraider/\" title=\"Tomb Raider series\">Tomb Raider series</a></li>\r\n	<li style=\"width:118px; height:86px; float:left; overflow:hidden; background-image:url(\'{{$STYLE_IMAGES_URL}}/top_menu_jolie.jpg\'); background-repeat:no-repeat;\"><a href=\"{{$SITE_URL}}/jolie/\" title=\"Angelina Jolie\">Angelina Jolie</a></li>\r\n	<li style=\"width:106px; height:86px; float:left; overflow:hidden; background-image:url(\'{{$STYLE_IMAGES_URL}}/top_menu_multimedia.jpg\'); background-repeat:no-repeat;\"><a href=\"{{$SITE_URL}}/multimedia/\" title=\"Multimedia\">Multimedia</a></li>\r\n	<li style=\"width:118px; height:86px; float:left; overflow:hidden; background-image:url(\'{{$STYLE_IMAGES_URL}}/top_menu_community.jpg\'); background-repeat:no-repeat;\"><a href=\"{{$SITE_URL}}/community/\" title=\"Community\">Community</a></li>\r\n</ul>','tpl',0),(12,'1',NULL,'global','rss',1272547216,'{{foreach from=$RSS item=RSS_LINK}}\r\n<link rel=\"alternate\" type=\"application/rss+xml\" title=\"{{$RSS_LINK.TITLE}}\" href=\"{{$RSS_LINK.HREF}}\" />\r\n{{/foreach}}','tpl',0),(13,'1',NULL,'global','search_form',1329661433,'<form action=\"\" method=\"post\" id=\"forms__search\">\r\n	<fieldset>\r\n		<label for=\'search__input\'>Search</label>\r\n		<input type=\"text\" id=\'search__input\' name=\"keyword\" class=\"search__keyword\" />\r\n		<input type=\'submit\' id=\'search__button\' value=\'Go\' />\r\n	</fieldset>\r\n</form>','tpl',0),(14,'1',NULL,'global','update',1329811535,'<div class=\"aside full_size\" id=\"system_console\"></div>\r\n<ul class=\"section full_size\">\r\n	<li><h2 class=\"full_size\">Updating Content <em>(id: {{$CONTENT.id}})</em>\r\n			<span class=\"description\"></span>\r\n		</h2>\r\n\r\n		<form id=\"forms__update\" action=\"\" method=\"post\">\r\n			{{foreach from=$CONTENT key=COLUMN item=VALUE}}\r\n			<fieldset class=\"deco_1\">\r\n				<legend title=\"{{$M_DFD.$COLUMN.label}}\">{{$M_DFD.$COLUMN.label}}</legend>\r\n				{{if $M_DFD.$COLUMN.type eq \'alphanumeric\'}}\r\n					{{if $M_DFD.$COLUMN.subtype eq \'string\'}}\r\n						{{if $M_DFD.$COLUMN.maxlength lte 255}}\r\n							<label title=\"{{$M_DFD.$COLUMN.label}}\" for=\"update__{{$M_DFD.$COLUMN.name}}\" class=\"full_size\"><strong>{{$M_DFD.$COLUMN.label}}:</strong><em>A {{if $M_DFD.$COLUMN.is_required}}required {{/if}}field with max. {{$M_DFD.$COLUMN.maxlength}} characters. No HTML markup allowed!</em></label>\r\n							<input\r\n								type=\"text\"\r\n								class=\"text full_size {{if $M_DFD.$COLUMN.is_required}}required{{/if}}\"\r\n								id=\"update__{{$M_DFD.$COLUMN.name}}\"\r\n								name=\"{{$M_DFD.$COLUMN.name}}\"\r\n								value=\"{{$VALUE}}\"\r\n								maxlength=\"{{$M_DFD.$COLUMN.maxlength}}\" />\r\n						{{else}}\r\n							<label class=\"full_size\" title=\"{{$M_DFD.$COLUMN.label}}\" for=\"update__{{$M_DFD.$COLUMN.name}}\"><strong>{{$M_DFD.$COLUMN.label}}:</strong><em>A {{if $M_DFD.$COLUMN.is_required}}required {{/if}}field with max. {{$M_DFD.$COLUMN.maxlength}} characters.{{if $M_DFD.$COLUMN.html_allowed}} Limited usage of HTML markup allowed!{{else}} No HTML markup allowed!{{/if}}</em></label>\r\n							{{if $M_DFD.$COLUMN.is_html_allowed}}\r\n								<textarea\r\n									class=\"full_size tinymce {{if $M_DFD.$COLUMN.is_required}}required{{/if}}\"\r\n									name=\"{{$M_DFD.$COLUMN.name}}\"\r\n									id=\"update__{{$M_DFD.$COLUMN.name}}\"\r\n									cols=\"\"\r\n									rows=\"\">{{$VALUE}}</textarea>\r\n							{{else}}\r\n								<textarea\r\n									class=\"full_size {{if $M_DFD.$COLUMN.is_required}}required{{/if}}\"\r\n									name=\"{{$M_DFD.$COLUMN.name}}\"\r\n									id=update__{{$M_DFD.$COLUMN.name}}\"\r\n									cols=\"\"\r\n									rows=\"\">{{$VALUE}}</textarea>\r\n							{{/if}}\r\n						{{/if}}\r\n					{{/if}}\r\n				{{/if}}\r\n			</fieldset>\r\n			<div class=\"closing\"></div>\r\n			{{/foreach}}\r\n\r\n			<fieldset class=\"buttons\">\r\n			<input type=\"hidden\" name=\"do\" value=\"update\" />\r\n			<input type=\"hidden\" name=\"m_unique_id\" value=\"{{$CONTENT.content.m_unique_id}}\" />\r\n			<input type=\"submit\" value=\"Update\" />\r\n			<input type=\"reset\" value=\"Reset Form\" />\r\n			<!-- <input type=\"button\" value=\"Cancel &amp; Close Form\" /> -->\r\n			</fieldset>\r\n\r\n		</form>\r\n	</li>\r\n</ul>','tpl',0),(15,'1',NULL,'global','update',1329811563,'$( document ).ready(\r\n	function() {\r\n		$(\'TEXTAREA.tinymce\').tinymce({\r\n			// Location of TinyMCE script\r\n			script_url : \'/public/js/tiny_mce/tiny_mce.js\',\r\n\r\n			// General options\r\n			theme : \"advanced\",\r\n			plugins : \"safari,advlink,searchreplace,contextmenu,paste,fullscreen,noneditable,visualchars,xhtmlxtras\",\r\n\r\n			// Theme options\r\n			theme_advanced_buttons1 : \"newdocument,|,cut,copy,paste,pastetext,|,search,replace,|,bold,italic,underline,strikethrough,|,bullist,numlist,|,outdent,indent,blockquote,|,undo,redo,|,link,unlink,cleanup,code,|,removeformat,|,sub,sup,|,charmap,fullscreen\",\r\n			theme_advanced_buttons2 : \"\",\r\n			theme_advanced_buttons3 : \"\",\r\n			theme_advanced_toolbar_location : \"top\",\r\n			theme_advanced_toolbar_align : \"center\",\r\n			theme_advanced_statusbar_location : \"none\",\r\n			theme_advanced_resizing : false\r\n		});\r\n\r\n		// Open \"Create Subroutine\" form\r\n\r\n		$(\"FORM#forms__modules__subroutine_list INPUT[type=\'button\']:eq(0)\").click(\r\n			function(event){\r\n				// Resetting errors\r\n				$(\"FORM#forms__modules__create_subroutine *\").removeClass(\"error\");\r\n				$(\"#system_console\").html( \"Required fields (in red) must be filled-in!\" ).removeClass(\"error success\").addClass(\"notice\").show();\r\n\r\n				// Hiding other forms\r\n				$(\"LI#sec_content_modules__ddl_alter_add\").hide();\r\n				$(\"LI#sec_content_modules__rename\").hide();\r\n\r\n				// Resetting form\r\n				$(\"FORM#forms__modules__create_subroutine INPUT[type=\'reset\']\").click();\r\n\r\n				// Opening form\r\n				if ( $(\"LI#sec_content_modules__create_subroutine\").is(\":hidden\") )\r\n				{\r\n					$(\"LI#sec_content_modules__create_subroutine\").slideDown(750);\r\n				}\r\n			}\r\n		);\r\n\r\n		// Close \"Create Subroutine\" form\r\n\r\n		$(\"FORM#forms__modules__create_subroutine INPUT[type=\'button\']:eq(0)\").click(\r\n			function(event){\r\n				$(\"FORM#forms__modules__create_subroutine INPUT[type=\'reset\']\").click();\r\n				$(\"#system_console\").hide();\r\n				$(\"LI#sec_content_modules__create_subroutine\").slideUp(1000);\r\n			}\r\n		);\r\n\r\n		// Submit \"Create Subroutine\" form\r\n\r\n		$(\"FORM#forms__modules__create_subroutine\").submit(\r\n			function(event){\r\n				event.preventDefault();\r\n				// IF limit==1 , we don\'t need sorting\r\n				if ( $(\"INPUT#create_subroutine__fetch_criteria__limit\").val() == \'1\' )\r\n				{\r\n					if ( $(\"SPAN.create_subroutine__sort_by:eq(0)\").is(\":visible\") )\r\n					{\r\n						$(\"SPAN.create_subroutine__sort_by:eq(0)\").hide();\r\n					}\r\n					$(\"SPAN.create_subroutine__sort_by:visible\").remove();\r\n					$(\"A#create_subroutine__fetch_criteria__add_sorting_button\").hide();\r\n					$(\"SELECT#create_subroutine__fetch_criteria__do_perform_sorting\").val(\"0\");\r\n				}\r\n				// Resetting errors\r\n				$(\"FORM#forms__modules__create_subroutine *\").removeClass(\"error\");\r\n				// Action\r\n				$(\"FORM#forms__modules__create_subroutine INPUT[name=\'do\']\").val(\"create_subroutine\");\r\n				$_url = window.location.href + \"?output=json\";\r\n				$.ajax({\r\n					type:\"POST\",\r\n					url:$_url,\r\n					dataType:\"json\",\r\n					data:$(\"FORM#forms__modules__create_subroutine\").serialize() + \"&\" + $(\"FORM#forms__modules__create_subroutine INPUT[name=\'s_ddl_information\']\").val(),\r\n					cache:false,\r\n					timeout:15000,\r\n					error:function( xhr, text_status, error ){\r\n							$(\"#system_console\").html(\"Unspecified error occured! Possible causes are <i>connection problems</i> or <i>invalid response from server</i>.\").removeClass(\"notice success\").addClass(\"error\");\r\n							return 0;\r\n						},\r\n					success:function( data )\r\n					{\r\n						if ( data.responseCode == \'1\' )\r\n						{\r\n							$(\"#system_console\").html( data.responseMessage ).removeClass(\"notice error\").addClass(\"success\").show();\r\n							setTimeout( \"window.location = window.location.href\", 2000 );\r\n							return 1;\r\n						}\r\n						else\r\n						{\r\n							faultMessageList = [];\r\n							for ( i = 0 ; i < data.length ; i++ )\r\n							{\r\n								switch ( data[ i ].faultCode )\r\n								{\r\n									case 700:\r\n										$(\"FORM#forms__modules__create_subroutine SELECT[name=\'s_data_definition[]\']\").addClass(\"error\");\r\n										break;\r\n									case 701:\r\n										$(\"FORM#forms__modules__create_subroutine INPUT[name=\'s_name\']\").addClass(\"error\");\r\n										break;\r\n									case 702:\r\n										$(\"FORM#forms__modules__create_subroutine INPUT[name=\'s_pathinfo_uri_schema\']\").addClass(\"error\");\r\n										break;\r\n									case 704:\r\n										$(\"FORM#forms__modules__create_subroutine TEXTAREA[name=\'s_fetch_criteria[policies][\" + data[ i ].faultExtra + \"]\']\").addClass(\"error\");\r\n										break;\r\n									case 705:\r\n										$(\"FORM#forms__modules__create_subroutine INPUT[name=\'s_fetch_criteria[rules][\" + data[ i ].faultExtra + \"][value]\']\").addClass(\"error\");\r\n										break;\r\n									case 706:\r\n										$(\"FORM#forms__modules__create_subroutine INPUT[name=\'s_fetch_criteria[limit]\']\").addClass(\"error\");\r\n										break;\r\n									case 707:\r\n										$(\"FORM#forms__modules__create_subroutine INPUT[name=\'s_fetch_criteria[pagination]\']\").addClass(\"error\");\r\n										break;\r\n									case 708:\r\n										$(\"FORM#forms__modules__create_subroutine SELECT[name=\'s_fetch_criteria[sort_by][\" + data[ i ].faultExtra + \"][field_name]\']\").addClass(\"error\");\r\n										break;\r\n								}\r\n								faultMessageList.push( \"<li>\" + data[ i ].faultMessage + \"</li>\" );\r\n							}\r\n							faultMessageParsed = \"One or more error(s) occured! Correct them and resubmit the form to continue:<ul>\" + faultMessageList.join(\"\") + \"</ul>\"\r\n							$(\"#system_console\").html( faultMessageParsed ).removeClass(\"notice success\").addClass(\"error\").show();\r\n						}\r\n					}\r\n				});\r\n			}\r\n		);\r\n\r\n		// Reset \"Create Subroutine\" form\r\n\r\n		$(\"FORM#forms__modules__create_subroutine INPUT[type=\'reset\']\").click(\r\n			function(event){\r\n				// Hiding stuff that should to be hidden normally\r\n				$(\"SPAN.create_subroutine__fetch_criteria__policies:eq(0)\").hide();\r\n				$(\"A#create_subroutine__fetch_criteria__add_policy_button\").hide();\r\n				$(\"SPAN.pre_extra\").hide();\r\n				$(\"SPAN.create_subroutine__sort_by:eq(0)\").hide();\r\n				$(\"A#create_subroutine__fetch_criteria__add_sorting_button\").hide();\r\n				// Removing excessive stuff\r\n				$(\"SPAN.create_subroutine__fetch_criteria__policies:eq(0)\").siblings(\"SPAN.create_subroutine__fetch_criteria__policies\").remove();\r\n				$(\"SPAN.extra:eq(0)\").siblings(\"SPAN.extra\").remove();\r\n				$(\"SPAN.create_subroutine__sort_by:eq(0)\").siblings(\"SPAN.create_subroutine__sort_by\").remove();\r\n				// Resetting errors\r\n				$(\"#system_console\").html( \"Required fields (in red) must be filled-in!\" ).removeClass(\"error success\").addClass(\"notice\").show();\r\n				$(\"FORM#forms__modules__create_subroutine *\").removeClass(\"error\");\r\n				// Disabling service mode\r\n				$(\"FORM#forms__modules__create_subroutine SELECT[name=\'s_service_mode\']\").prop(\"disabled\",\"disabled\");\r\n			}\r\n		);\r\n	}\r\n);','js',0),(16,'1','{4F54D297-5867BF9A-17873F81-1A94BEA6}','mod','components_modules',1329811827,'<div class=\"aside full_size\" id=\"system_console\"></div>\r\n<ul class=\"section full_size\">\r\n	<li id=\"components__modules__alter_add\" class=\"ondemand\">\r\n		<h2>Register a Module\r\n			<span class=\"description\"></span>\r\n		</h2>\r\n		<form id=\"forms__modules__alter_add\" class=\"js__go_ajax\" action=\"\" method=\"post\">\r\n			<fieldset class=\"m_name\">\r\n				<label title=\"Module Name\" for=\"create_module__m_name\"><strong>Module Name:</strong></label>\r\n				<input type=\"text\" class=\"text required _701\" id=\"create_module__m_name\" name=\"m_name\" value=\"\" maxlength=\"32\" />\r\n				<em class=\"ui-tooltip\">Alphanumeric and underscore characters only [a-zA-Z0-9_].</em>\r\n			</fieldset>\r\n			<fieldset class=\"m_description\">\r\n				<label title=\"Module Description\" for=\"create_module__m_description\"><strong>Module Description:</strong></label>\r\n				<input type=\"text\" class=\"text required _702\" id=\"create_module__m_description\" name=\"m_description\" value=\"\" maxlength=\"255\" />\r\n				<em class=\"ui-tooltip\">Brief description of module [its purpose, function etc].</em>\r\n			</fieldset>\r\n			<fieldset class=\"m_extras\">\r\n				<label title=\"Features to Embed\" for=\"create_module__m_extras\"><strong>Features to Embed:</strong></label>\r\n				<select name=\"m_extras[]\" multiple=\"multiple\" size=\"3\" id=\"create_module__m_extras\">\r\n					<option value=\"tags\">Content Tagging &amp; Labeling</option>\r\n					<option value=\"comments\">User Comments</option>\r\n				</select>\r\n				<em class=\"ui-tooltip\">Additional features to be embedded into the structure of this module.</em>\r\n			</fieldset>\r\n			<fieldset class=\"m_enforce_ssl\">\r\n				<label title=\"Enforce secure (SSL) connection?\"><strong>Enforce secure (SSL) connection?</strong></label>\r\n				<span class=\"input ui-buttonset\">\r\n					<input type=\"radio\" name=\"m_enforce_ssl\" id=\"yes_for__m_enforce_ssl\" value=\"1\" />\r\n					<input type=\"radio\" name=\"m_enforce_ssl\" id=\"no_for__m_enforce_ssl\" value=\"0\" />\r\n					<label for=\"yes_for__m_enforce_ssl\" class=\"js__trigger_on_change\" title=\"Yes\">Yes</label>\r\n					<label for=\"no_for__m_enforce_ssl\" class=\"js__trigger_on_change\" title=\"No\">No</label>\r\n				</span>\r\n				<em class=\"ui-tooltip\">Whether to enforce SSL connection for this module.</em>\r\n			</fieldset>\r\n			<fieldset class=\"m_enable_caching\">\r\n				<label title=\"Enable Page Caching?\"><strong>Enable Page Caching?</strong></label>\r\n				<span class=\"input ui-buttonset\">\r\n					<input type=\"radio\" name=\"m_enable_caching\" id=\"yes_for__m_enable_caching\" value=\"1\" />\r\n					<input type=\"radio\" name=\"m_enable_caching\" id=\"no_for__m_enable_caching\" value=\"0\" />\r\n					<label for=\"yes_for__m_enable_caching\" class=\"js__trigger_on_change\" title=\"Yes\">Yes</label>\r\n					<label for=\"no_for__m_enable_caching\" class=\"js__trigger_on_change\" title=\"No\">No</label>\r\n				</span>\r\n				<em class=\"ui-tooltip\">Whether to enable page caching for the module pages or not.</em>\r\n			</fieldset>\r\n\r\n			<div class=\"system_console\"></div>\r\n			<fieldset class=\"buttons\">\r\n				<input type=\"hidden\" name=\"do\" value=\"\" />\r\n				<input type=\"hidden\" name=\"m_unique_id\" value=\"\" />\r\n				<input type=\"button\" value=\"Submit\" />\r\n				<input type=\"reset\" value=\"Clear Form\" />\r\n				<input type=\"button\" value=\"Cancel &amp; Close\" />\r\n			</fieldset>\r\n		</form>\r\n	</li>\r\n\r\n	<li id=\"components__modules__list\">\r\n		<h2>Components Management - Modules\r\n			<span class=\"description\"></span>\r\n		</h2>\r\n\r\n		<form id=\"forms__modules__list\" class=\"js__go_ajax\" method=\"post\" action=\"\">\r\n		<table class=\"full_size tablesorter\" id=\"tables__modules__list\">\r\n			<thead>\r\n				<tr>\r\n					<th style=\"width:65%; white-space:nowrap\">Module Info</th>\r\n					<th style=\"width:35%; white-space:nowrap; text-align:center;\" class=\"{sorter: false}\">Preliminary Diagnostics</th>\r\n				</tr>\r\n			</thead>\r\n			<tfoot>\r\n				<tr>\r\n					<td colspan=\"2\">\r\n					<div class=\"system_console\"></div>\r\n					<fieldset class=\"buttons\">\r\n						<input type=\"hidden\" value=\"\" name=\"do\" />\r\n						<input type=\"hidden\" value=\"\" name=\"m_unique_id\" />\r\n						<input type=\"button\" value=\"Create New\" />\r\n					</fieldset>\r\n					</td>\r\n				</tr>\r\n			</tfoot>\r\n			<tbody>\r\n				{{foreach from=$CONTENT item=MODULE}}\r\n				{{if $MODULE.m_type neq \'built-in\'}}\r\n				<tr>\r\n					<td>\r\n						<span class=\"name\">\r\n							<a href=\"{{$MODULE_URL}}/components/viewmodule-{{$MODULE.m_unique_id|m_unique_id_clean}}\"><strong>/{{$MODULE.m_name|truncate:16}}</strong></a> - {{$MODULE.m_description|truncate:48}}\r\n							<br />\r\n							{{$MODULE.m_unique_id}}\r\n						</span>\r\n						<ul class=\"actions\">\r\n							<li class=\"ui-icon ui-icon-pencil\"><a class=\"edit\" href=\"?{{$MODULE.m_unique_id}}\" title=\"Edit\">Edit</a></li>\r\n							<li class=\"ui-icon ui-icon-closethick\"><a class=\"delete\" href=\"?{{$MODULE.m_unique_id}}\" title=\"Delete\">Delete</a></li>\r\n							<li class=\"ui-icon ui-icon-seek-next\"><a href=\"{{$MODULE_URL}}/components/viewmodule-{{$MODULE.m_unique_id|m_unique_id_clean}}\" title=\"Manage\">Manage</a></li>\r\n						</ul>\r\n					</td>\r\n					<td style=\"text-align:center;\">COMING SOON!</td>\r\n				</tr>\r\n				{{/if}}\r\n				{{foreachelse}}\r\n				<tr>\r\n					<td colspan=\"2\"><span class=\"system_message_error\">NO MODULES FOUND!</span></td>\r\n				</tr>\r\n				{{/foreach}}\r\n			</tbody>\r\n		</table>\r\n		</form>\r\n	</li>\r\n</ul>','tpl',0),(17,'1','{4F54D297-5867BF9A-17873F81-1A94BEA6}','mod','components_modules',1329811827,'/**\r\n * Local Javascript object\r\n *\r\n * @returns object Object handler\r\n */\r\nfunction /* Class */ Acp_Components ()\r\n{\r\n	return {\r\n		constructor : Acp_Components,\r\n		obj : null\r\n	};\r\n};\r\n\r\n/* Extending with Persephone */\r\nextend(Acp_Components, Persephone);\r\n\r\n/* Instantiating Class handler */\r\nvar acp__components = Acp_Components.singleton();\r\n\r\njQuery(document).ready(function ()\r\n{\r\n	/**\r\n	 * Opens \"Create-Module\" Form.\r\n	 */\r\n	jQuery(\"#forms__modules__list INPUT[type=\'button\']:eq(0)\").click(function ( event )\r\n	{\r\n		if ( jQuery(\"#components__modules__alter_add\").is(\":hidden\") )\r\n		{\r\n			/* Close all .ondemand panes */\r\n			jQuery(\"#content UL.data_container > .ondemand\").hide();\r\n		}\r\n\r\n		/* Open \"DDL-Create\" form */\r\n		jQuery(\"#components__modules__alter_add\").slideDown(\"medium\", function ()\r\n		{\r\n			/* Reset \"DDL-Create\" Form */\r\n			jQuery(\"#forms__modules__alter_add\").trigger(\"reset\");\r\n		});\r\n\r\n		/* do = create */\r\n		jQuery(\"#forms__modules__alter_add [name=\'do\']\").val(\"create\");\r\n\r\n		/* Make-up */\r\n		jQuery(\"#components__modules__alter_add H2\").html( \"Register a Module\" ); // Form title\r\n	});\r\n\r\n	/**\r\n	 * Resets \"Create-Module\" Form to its clean and default state.\r\n	 */\r\n	jQuery(\"#forms__modules__alter_add\").bind(\"reset\", function ( event )\r\n	{\r\n		var whoami = acp__components.Form.self = jQuery(\"#forms__modules__alter_add\");\r\n		whoami.find(\".error\").removeClass(\"error\"); // Resetting errors\r\n		acp__components.Form.closeConsoles();\r\n\r\n		/* Cleanup */\r\n		whoami.find(\"INPUT[type=\'text\']\").val(\"\"); // Clear all INPUT text-fields\r\n		whoami.find(\"SELECT > OPTION\").each(function () // Getting all SELECT>OPTION s de-selected\r\n		{\r\n			jQuery(this).get(0).selected = false;\r\n			return;\r\n		});\r\n		whoami.find(\"INPUT[type=\'radio\']\").each(function () // Getting all Radios un-checked\r\n		{\r\n			jQuery(this).get(0).checked = false;\r\n			return;\r\n		});\r\n		jQuery(\"#forms__modules__alter_add [name=\'m_unique_id\']\").val(\"\").prop(\"disabled\",\"disabled\");\r\n\r\n		/* Scroll to global Console */\r\n		var currentConsole = acp__components.Form.scrollToConsole(true);\r\n		currentConsole.html(\"Required fields (in red) must be filled-in!\").removeClass(\"error success\").show();\r\n\r\n		/* Stop \"reset\" event from occuring... This is our show :-) */\r\n		event.preventDefault();\r\n	});\r\n\r\n	/**\r\n	 * Submits \"Create-Module\" and \"Edit-Module\" Forms.\r\n	 */\r\n	jQuery(\"#forms__modules__alter_add INPUT[type=\'button\']:eq(0)\").click(function ( event )\r\n	{\r\n		event.preventDefault();\r\n		var whoami = acp__components.Form.self = jQuery(\"#forms__modules__alter_add\");\r\n		whoami.trigger(\"submit\"); // Submit the form via AJAX (.js__go_ajax), it will handle itself...\r\n	});\r\n\r\n	/**\r\n	 * Close \"Module-Create\" Form.\r\n	 */\r\n	jQuery(\"#forms__modules__alter_add INPUT[type=\'button\']:eq(1)\").click(function ( event )\r\n	{\r\n		if ( jQuery(\"#components__modules__alter_add\").is(\":visible\") )\r\n		{\r\n			acp__components.Form.closeConsoles();\r\n			jQuery(\"#components__modules__alter_add\").slideUp(\"medium\");\r\n		}\r\n	});\r\n\r\n	/**\r\n	 * Prepares \"Edit-Module\" form.\r\n	 */\r\n	jQuery(\"FORM#forms__modules__list A.edit\").click(function ( event )\r\n	{\r\n		event.preventDefault();\r\n\r\n		/* Close all .ondemand panes */\r\n		jQuery(\"#content UL.data_container > .ondemand\").hide();\r\n\r\n		/* The usual... */\r\n		var whoami = acp__components.Form.self = jQuery(\"#components__modules__alter_add\"); // Working form\r\n		var currentConsole = acp__components.Form.scrollToConsole(true);\r\n		whoami.trigger(\"reset\"); // Reset form\r\n		currentConsole.html(\"Resetting forms and fetching module information! Please stand-by...\").removeClass(\"error success\");\r\n\r\n		/* Few minor things... */\r\n		var m_unique_id_clean = jQuery(this).attr(\"href\").replace(/^\\?/,\"\").replace( /[^a-z0-9]/gi , \"\" ).toLowerCase();\r\n		var url = \"{{$MODULE_URL}}/components/viewmodule-\" + m_unique_id_clean;\r\n\r\n		/* Execute */\r\n		jQuery.ajax({\r\n			type : \"GET\",\r\n			url : url,\r\n			data : null,\r\n			cache : false,\r\n			success : function ( data )\r\n			{\r\n				if ( data.me == undefined )\r\n				{\r\n					var faultMessage = \"One or more error(s) occured! Correct them and resubmit the form to continue:<ul><li>No such module (id: \" + m_unique_id + \") found!</li></ul>\";\r\n					currentConsole.html(faultMessage).removeClass(\"success\").addClass(\"error\");\r\n					return 0;\r\n				}\r\n				else\r\n				{\r\n					/* Make-up */\r\n					jQuery(\"#components__modules__alter_add H2\").html( \"Editing Module <em>/\" + data.me.m_name + \"</em>\" ); // Form title\r\n\r\n					/* Values */\r\n					var dataset_to_apply = {\r\n						\'m_name\' : data.me.m_name,\r\n						\'m_description\' : data.me.m_description,\r\n						\'m_extras\' : data.me.m_extras,\r\n						\'m_enforce_ssl\' : data.me.m_enforce_ssl,\r\n						\'m_enable_caching\' : data.me.m_enable_caching\r\n					};\r\n					jQuery.each(dataset_to_apply, function ( key , value )\r\n					{\r\n						var input_field = jQuery(\"#forms__modules__alter_add .\" + key + \" [name^=\'\" + key + \"\']\");\r\n						if ( input_field.length == 1 )\r\n						{\r\n							if ( value != null )\r\n							{\r\n								/* SELECTs... */\r\n								if ( input_field.is(\"SELECT\") )\r\n								{\r\n									if ( !( value instanceof Array ) )\r\n									{\r\n										value = value.split(\",\");\r\n									}\r\n									input_field.val(value);\r\n								}\r\n								/* Everything else... */\r\n								else if ( input_field.is(\"INPUT[type=\'text\']\") )\r\n								{\r\n									input_field.val(acp__components.Form.decodeHtmlEntities(value));\r\n								}\r\n							}\r\n						}\r\n						else if ( input_field.length > 1 )\r\n						{\r\n							input_field.each(function ( index )\r\n							{\r\n								if ( jQuery(this).is(\":radio\") )\r\n								{\r\n									/* We have RADIO buttons here */\r\n									jQuery(this).get(0).checked = false; // jQuery-UI: Although this doesn\'t work, I will leave it here...\r\n									if ( jQuery(this).attr(\"value\") == value )\r\n									{\r\n										jQuery(this).get(0).checked = true; // jQuery-UI: Although this doesn\'t work, I will leave it here...\r\n										jQuery(\"LABEL[for=\'\" + jQuery(this).attr(\"id\") + \"\']\").click();\r\n									}\r\n								}\r\n							});\r\n						}\r\n					});\r\n\r\n					/* do = edit */\r\n					jQuery(\"#forms__modules__alter_add [name=\'do\']\").val(\"edit\");\r\n					jQuery(\"#forms__modules__alter_add [name=\'m_unique_id\']\").prop(\"disabled\",\"\").val(data.me.m_unique_id);\r\n\r\n					/* Finalize */\r\n					currentConsole.html(\"Form successfully loaded!\").removeClass(\"error\").addClass(\"success\");\r\n					jQuery(\"#components__modules__alter_add\").slideDown(\"medium\");\r\n\r\n					return 1;\r\n				}\r\n			}\r\n		});\r\n	});\r\n\r\n	/**\r\n	 * Executes \"Delete-Module\" Request\r\n	 */\r\n	jQuery(\"#forms__modules__list A.delete\").click(function ( event )\r\n	{\r\n		event.preventDefault();\r\n		var m_unique_id = jQuery(this).attr(\"href\").replace(/^\\?/,\"\");\r\n\r\n		/* \"Are you sure?\" */\r\n		acp__components.Form.getDialogObject({\r\n			body : \"You are about to <b>delete</b> a module, alongside with its <i>subroutines</i> and all associated data!<br /><b>THIS ACTION IS IRREVERSIBLE!!!</b><br /><br />Shall I proceed?\",\r\n			buttons : [\r\n				{\r\n					text : \"Yes, *delete* the module, including its *content-base*!\",\r\n					click : function ( event )\r\n					{\r\n						jQuery(this).unbind(\"dialogclose\").dialog(\"close\");\r\n						jQuery(\"#forms__modules__list [name=\'do\']\").val(\"delete\");\r\n						jQuery(\"#forms__modules__list [name=\'m_unique_id\']\").val(m_unique_id);\r\n						var whoami = acp__components.Form.self = jQuery(\"#forms__modules__list\");\r\n						whoami.trigger(\"submit\"); // Submit the form via AJAX (.js__go_ajax), it will handle itself...\r\n					}\r\n				},\r\n				{\r\n					text : \"No, stop!\",\r\n					click : function ( event )\r\n					{\r\n						jQuery(this).dialog(\"close\");\r\n						acp__components.Form.getDialogObject({\r\n							body : \"Request aborted by user! No actions performed!\",\r\n							buttons : [\r\n								{\r\n									text : \"Thanks!\",\r\n									click : function ( event )\r\n									{\r\n										jQuery(this).dialog(\"close\");\r\n									}\r\n								}\r\n							]\r\n						}).dialog(\"open\").unbind(\"dialogclose\");\r\n					}\r\n				}\r\n			],\r\n			width : 500\r\n		}).dialog(\"open\");\r\n	});\r\n});','js',0),(18,'1','{4F54D297-5867BF9A-17873F81-1A94BEA6}','mod','components_modules',1329811827,'UL.actions\r\n{\r\n	float:right;\r\n	clear:right;\r\n	margin-left:7px;\r\n	height:100%;\r\n	width:auto;\r\n	max-width:64px;\r\n	display:none; \r\n}\r\n\r\nTD:hover > UL.actions\r\n{\r\n	display:block;\r\n}\r\n\r\n	UL.actions > LI\r\n	{\r\n		float:left;\r\n		clear:none;\r\n	}\r\n	\r\n		UL.actions > LI > A\r\n		{\r\n			float:left;\r\n			clear:both;\r\n			width:100%;\r\n			height:100%;\r\n			\r\n			text-indent:-99999px;\r\n		}\r\n\r\nSPAN.name\r\n{\r\n	height:inherit;\r\n	width:543px;\r\n	float:left;\r\n	clear:left;\r\n	overflow:hidden;\r\n	white-space:nowrap;\r\n	vertical-align:middle;\r\n\r\n	line-height:18px;\r\n}\r\n\r\nTD:hover > SPAN.name\r\n{\r\n	width:470px;\r\n}\r\n\r\n	SPAN.name STRONG\r\n	{\r\n		color:#0085BF;\r\n	}','css',0),(22,'1','{4F54D297-5867BF9A-17873F81-1A94BEA6}','mod','header',1329812130,'<div id=\"header\" class=\"acp\"></div>','tpl',0),(23,'1','{4F54D297-5867BF9A-17873F81-1A94BEA6}','mod','logo',1329812105,'<h1 id=\"logo\" class=\"acp\"><a href=\"{{$MODULE_URL}}\" title=\"Persephone Control Panel\">{{$SITE_NAME}}{{if $SITE_MOTTO neq \'\'}} - {{$SITE_MOTTO}}{{/if}}</a></h1>','tpl',0),(24,'1','{4F54D297-5867BF9A-17873F81-1A94BEA6}','mod','content_and_data__index',1282201054,'<samp id=\"system_console\" class=\"full_size\"></samp>\r\n<ul class=\"data_container full_size\">\r\n	<li id=\"sec_content_modules__list\">\r\n		<h2 class=\"full_size\">Content Management - Modules\r\n			<span class=\"description\"></span>\r\n		</h2>\r\n\r\n		<form id=\"forms__modules__list\" method=\"post\" action=\"\">\r\n		<table class=\"full_size tablesorter\" id=\"tables__module_list\" cellpadding=\"0\" cellspacing=\"0\">\r\n			<thead>\r\n				<tr>\r\n					<th style=\"width:40%; white-space:nowrap\">Module Info</th>\r\n					<th style=\"width:40%; white-space:nowrap; text-align:center\">Module Unique-Id</th>\r\n					<th style=\"width:15%; white-space:nowrap; text-align:center\">(De)Activate?</th>\r\n					<th style=\"width:5%; text-align:center\"></th>\r\n				</tr>\r\n			</thead>\r\n			<tfoot>\r\n				<tr>\r\n					<td colspan=\"4\">\r\n					<span class=\"buttons\">\r\n					<input type=\"button\" value=\"Content Management\" />\r\n					</span>\r\n					</td>\r\n				</tr>\r\n			</tfoot>\r\n			<tbody>\r\n				{{foreach from=$CONTENT item=MODULES}}\r\n				{{if $MODULES.m_type neq \'built-in\'}}\r\n				<tr>\r\n					<td><strong>/{{$MODULES.m_name|truncate:32}}</strong><em>{{$MODULES.m_description|truncate:48}}</em></td>\r\n					<td style=\"text-align:center\">{{$MODULES.m_unique_id}}</td>\r\n					<td style=\"text-align:center\">{{if $MODULES.m_is_enabled eq \'1\'}}<img src=\"{{$STYLE_IMAGES_URL}}/button_ok_1_16.png\" alt=\"Yes\" class=\"absmiddle\" />\r\n					{{else}}<img src=\"{{$STYLE_IMAGES_URL}}/button_no_1_16.png\" alt=\"No\" class=\"absmiddle\" />\r\n					{{/if}}\r\n					</td>\r\n					<td style=\"text-align:center\">{{if $MODULES.m_can_remove eq \'1\' and $MODULES.m_is_enabled eq \'0\'}}<input type=\"radio\" name=\"m_unique_id\" value=\"{{$MODULES.m_unique_id}}\" class=\"radio\" />{{/if}}</td>\r\n				</tr>\r\n				{{/if}}\r\n				{{foreachelse}}\r\n				<tr>\r\n					<td colspan=\"4\"><span class=\"system_message_error\">NO MODULES FOUND!</span></td>\r\n				</tr>\r\n				{{/foreach}}\r\n			</tbody>\r\n		</table>\r\n		</form>\r\n	</li>\r\n\r\n	<li id=\"media_library\">\r\n		<h2 class=\"full_size\">Media Library\r\n			<span class=\"description\"></span>\r\n		</h2>\r\n\r\n		<form id=\"forms__media_library\" method=\"post\" action=\"\">\r\n		<div id=\"media_library_sneak_peek\"></div>\r\n		<table class=\"full_size tablesorter\" id=\"tables__media_library\" cellpadding=\"0\" cellspacing=\"0\">\r\n			<thead>\r\n				<tr>\r\n					<th style=\"width:7%; text-align:right;\">Id</th>\r\n					<th style=\"width:25%; white-space:nowrap;\">Checksum</th>\r\n					<th style=\"width:10%; white-space:nowrap;\">Type</th>\r\n					<th style=\"width:10%; white-space:nowrap;\">Size</th>\r\n					<th style=\"width:15%; text-align:center; white-space:nowrap;\">Date Modified</th>\r\n					<th style=\"width:33%; white-space:nowrap;\">Actions</th>\r\n				</tr>\r\n			</thead>\r\n			<tfoot>\r\n				<tr>\r\n					<td colspan=\"6\">										\r\n					<div class=\"pager\">\r\n					{{pager total_nr_of_items=$CONTENT.media_library__total_nr_of_items nr_of_items_per_page=\"20\"}}\r\n					</div>\r\n					<span class=\"buttons\">\r\n					<input type=\"hidden\" name=\"current_page\" value=\"1\" />\r\n					<input type=\"button\" value=\"Delete Selected\" />\r\n					</span>\r\n					</td>\r\n				</tr>\r\n			</tfoot>\r\n			<tbody>\r\n				{{foreach from=$CONTENT.media_library__file_list item=FILE}}\r\n				<tr id=\"{{$FILE.f_id}}\">\r\n					<td style=\"text-align:right;\" class=\"w_preview_hover\">{{$FILE.f_id}}</td>\r\n					<td class=\"w_preview_hover\">{{$FILE.f_hash}}</td>\r\n					<td style=\"white-space:nowrap;\">{{$FILE.f_extension}}</td>\r\n					<td style=\"white-space:nowrap;\">{{$FILE.f_size|filesize_h}}B</td>\r\n					<td style=\"white-space:nowrap; text-align:center;\">{{$FILE.f_timestamp|date_format:\"%d-%m-%Y %R\"}}</td>\r\n					<td style=\"white-space:nowrap;\">\r\n					<ul class=\"horizontal\">\r\n					{{if $FILE.f_mime|filetype eq \'image\'}}\r\n						<li>thumb +</li>\r\n						<li>watermark +</li>\r\n					{{elseif $FILE.f_mime|filetype eq \'audio\'}}\r\n					Audio\r\n					{{elseif $FILE.f_mime|filetype eq \'video\'}}\r\n					Video\r\n					{{else}}\r\n					Other\r\n					{{/if}}\r\n						<li>delete -</li>\r\n					</ul>\r\n					</td>\r\n				</tr>\r\n				{{foreachelse}}\r\n				<tr>\r\n					<td colspan=\"6\"><span class=\"system_message_error\">No Files Found!</span></td>\r\n				</tr>\r\n				{{/foreach}}\r\n			</tbody>\r\n		</table>\r\n		</form>\r\n	</li>\r\n</ul>','tpl',0),(25,'1','{4F54D297-5867BF9A-17873F81-1A94BEA6}','mod','content_and_data__index',1279541422,'function tablesorter__module_list ()\r\n{\r\n	$(\"LI#media_library TABLE\")\r\n		.tablesorter(\r\n			{\r\n				/*\r\n				sortList:[[0,0]],\r\n				*/\r\n				headers:{\r\n					0:{ sorter:false },\r\n					1:{ sorter:false },\r\n					2:{ sorter:false },\r\n					3:{ sorter:false },\r\n					4:{ sorter:false },\r\n					5:{ sorter:false }\r\n				},\r\n				widgets: [\'zebra\'],\r\n				widthFixed: true\r\n			}\r\n		);\r\n}\r\n\r\n$( document ).ready(\r\n	function() {\r\n		// Tablesorting...\r\n\r\n		tablesorter__module_list();\r\n\r\n		// \"DDL Management\" action\r\n\r\n		$(\"FORM#forms__modules__list INPUT[type=\'button\']:eq(0)\").click(\r\n			function(event){\r\n				m_unique_id = $(\"FORM#forms__modules__list INPUT[name=\'m_unique_id\']:checked\").val();\r\n				if ( ! m_unique_id )\r\n				{\r\n					$(\"SAMP#system_console\").html(\"No module was selected! Please select one...\").removeClass(\"notice success\").addClass(\"error\").slideDown();\r\n					return 0;\r\n				}\r\n				m_unique_id_clean = m_unique_id.replace( /[^a-z0-9]/gi , \"\" ).toLowerCase();\r\n				window.location = \"{{$MODULE_URL}}/management/content-\" + m_unique_id_clean;\r\n			}\r\n		);\r\n		\r\n		$(\"TABLE#tables__media_library TBODY TR TD.w_preview_hover\").mouseover(\r\n			function ( event )\r\n			{\r\n				event.stopPropagation();\r\n				var _media_id = $(this).parent().attr(\"id\");\r\n				if ( _media_id != null )\r\n				{\r\n					$(\"DIV#media_library_sneak_peek\").css( \"background-image\",\"url(\'/static/stream/s-\" + _media_id + \"\')\" );\r\n				}\r\n				\r\n				$(this).mousemove(\r\n					function(event)\r\n					{\r\n						if ( event.pageY > 0 && event.pageX > 0 && event.pageX < $(document).width() && event.pageY < $(document).height() )\r\n						{\r\n							$(\"DIV#media_library_sneak_peek\")\r\n								.css( \"position\",\"absolute\" )\r\n								.css( \"top\",event.pageY )\r\n								.css( \"left\",event.pageX + 10 ).show( 0 )\r\n								.show( 0 );\r\n						}\r\n					}\r\n				);\r\n				\r\n				$(this).mouseout(\r\n					function(event)\r\n					{\r\n						$(\"DIV#media_library_sneak_peek\").hide();\r\n					}\r\n				);\r\n			}\r\n		);\r\n	}\r\n);','js',0),(26,'1','{4F54D297-5867BF9A-17873F81-1A94BEA6}','mod','content_and_data__index',1279542029,'#media_library_sneak_peek\r\n{\r\n	width: 100px;\r\n	height: 100px;\r\n	background-color: transparent;\r\n	background-position: left top;\r\n	background-repeat: no-repeat;\r\n	display: none;\r\n	position: absolute;\r\n	top: 0;\r\n	left: 0;\r\n	z-index: 99999;\r\n}\r\n\r\nDIV#content UL#side_pane LI#side_pane__banners__media_library\r\n{\r\n	width: 354px;\r\n	height: 70px;\r\n	clear: right;\r\n	background-image: url(\'{{$STYLE_IMAGES_URL}}/banner_settings.png\');\r\n	background-repeat: no-repeat;\r\n	background-position: 0 0;\r\n	\r\n	text-indent: -99999px;\r\n	list-style-image: none;\r\n	list-style-type: none;\r\n	list-style-position: inside;\r\n}\r\n\r\nDIV#content UL#side_pane LI#side_pane__banners__manage_content\r\n{\r\n	width: 354px;\r\n	height: 70px;\r\n	clear: right;\r\n	background-image: url(\'{{$STYLE_IMAGES_URL}}/banner_settings.png\');\r\n	background-repeat: no-repeat;\r\n	background-position: 0 0;\r\n	\r\n	text-indent: -99999px;\r\n	list-style-image: none;\r\n	list-style-type: none;\r\n	list-style-position: inside;\r\n}\r\n\r\nDIV#content UL.data_container LI TABLE#tables__media_library TBODY TR TD.w_preview_hover\r\n{\r\n	cursor: pointer;\r\n	height: 25px;\r\n}','css',0),(28,'1','{4F54D297-5867BF9A-17873F81-1A94BEA6}','mod','meta_generator',1329812046,'<meta name=\"keywords\"                 content=\"{{$GENERATOR.keywords}}\" />\r\n<meta name=\"description\"              content=\"{{$GENERATOR.description}}\" />\r\n<meta http-equiv=\"content-type\"       content=\"text/html; charset=utf-8\" />\r\n<meta name=\"robots\"                   content=\"noindex,nofollow,nocache\" />\r\n<meta name=\"verify-v1\"                content=\"{{$GENERATOR.google_verify_v1}}\" />','tpl',0),(29,'1','{4F54D297-5867BF9A-17873F81-1A94BEA6}','mod','navigation',1329812079,'<ul id=\"nav\">\r\n	<li style=\"width:113px; background-image:url(\'{{$STYLE_IMAGES_URL}}/acp__navigation__system.jpg\'); background-repeat:no-repeat;\">\r\n		<a href=\"#\" title=\"Configuration\">Configuration</a>\r\n		<ul style=\"color: #346C11; box-shadow: 0 0 3px 1px #346C11;\">\r\n			<li>\r\n				<a href=\"#\">System</a>\r\n				<ul style=\"color: #346C11; box-shadow: 0 0 3px 1px #346C11;\">\r\n					<li><a href=\"{{$MODULE_URL}}/configuration/system-cookies\">Cookies</a></li>\r\n					<li><a href=\"{{$MODULE_URL}}/configuration/system-email\">E-Mail Setup</a></li>\r\n					<li><a href=\"{{$MODULE_URL}}/configuration/system-general\">General Configuration</a></li>\r\n					<li><a href=\"{{$MODULE_URL}}/configuration/system-modules\">Module Management</a></li>\r\n					<li><a href=\"{{$MODULE_URL}}/configuration/system-security\">Security</a></li>\r\n					<li><a href=\"{{$MODULE_URL}}/configuration/system-serverenvironment\">Server Environment</a></li>\r\n					<li><a href=\"{{$MODULE_URL}}/configuration/system-performance\">